import React from 'react';
import './index.css';
import {API_ENDPOINTS} from "../utils/constants";
import {useEffect, useState} from "react";
import {API_DOMAIN} from "../utils/constants";
import { async } from 'q';
import Komentarai from '../Komentarai';
import {put} from "../utils/put";


function IrasasEdit(props){

    const irasid=props.value;

    const url=API_ENDPOINTS.vienasirasas+"/"+irasid;
    const [pavadinimas, setPavadinimas] = useState('pavadinimas');
    const [tekstas, setTekstas] = useState('tekstass');
    const [irasodata, setIrasoata] = useState('2019-01-01');

    const editIrasas = irasas => {
      setPavadinimas(irasas.pavadinimas);
      setTekstas(irasas.tekstas);
         };

    useEffect(() => {
        const updateInfo = async () => {
            const data = await fetch(`${API_DOMAIN}${url}`)
                .then(result => result.json())
            setPavadinimas(data.pavadinimas);
            setTekstas(data.tekstas);
            setIrasoata(data.irasodata);
                    };
        updateInfo();
    }, []);

 return (
     <div className="Irasas--visas">
         <h1 className="Irasas--Pavadinimas">{pavadinimas}</h1>
         <p className="Irasas--data">{irasodata}</p>
         <hr className="Irasas--separator" />
         <p className="Irasas--Tekstas"> {tekstas}</p>
          <EditIrasasForm  pavadinimas={pavadinimas} tekstas={tekstas} id={irasid} editIrasas={editIrasas}/>
         {/* <Komentarai /> */}
     </div>
 )
}
function EditIrasasForm(props) {
   console.log("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa");
  console.log(props);
  const id=props.id;
 const pavadinimas= props.pavadinimas;

console.log(props.pavadinimas);
 const tekstas=props.tekstas;
  const initialFormState = {id:{id}, pavadinimas:' ', tekstas:''};

  initialFormState.pavadinimas=props.pavadinimas;
    console.log(initialFormState);
    const [irasas, setIrasas] = useState(initialFormState);

    const handleInputChange = event => {
      const {name, value} = event.target;

      setIrasas({...irasas, [name]: value})
    };

    const editIrasas = async event => {
      event.preventDefault();
      if (!irasas.pavadinimas || !irasas.tekstas) return;

    const response = await put(API_ENDPOINTS.updateIrasas+"/"+props.id, irasas);

    props.editIrasas(response);

    };

    return (
      <form onSubmit={editIrasas}>
      <div className="formlaukas">
      <label>Pavadinimas</label>
      <textarea type="text" cols="80"  rows="3" name="pavadinimas" value={irasas.pavadinimas} onChange={handleInputChange}/>
      </div>
      <div  className="formlaukas">
     <label>Tekstas</label>
       <textarea type="text" cols="80"  rows="35" name="tekstas" value={irasas.tekstas} onChange={handleInputChange}/>
     </div>
        <button className="formlaukas">Įrašyti</button>
      </form>
    )
  }
export default IrasasEdit;
