import React, { useState } from 'react';
import './index.css';
import { useFetch } from "../utils/fetch-hook";
import { API_ENDPOINTS } from "../utils/constants";
import { put } from "../utils/put";


function Komentarai(props) {
  console.log(props);

  const komenturl = API_ENDPOINTS.komentaras + "/" + props.value;
  const { loading, data, setData } = useFetch(komenturl);

  const AddAtsakymas = koment => {
    setData([...data, koment])
  };

  return (
    <section className="Komentarai">
      <h5 className="komentaras">Komentarai redagavimui</h5>
      {loading ?
        <div>Loading...</div>
        :
        data.map(Item)
      }
      {/* <AddKomentForm addKomentaras={addKomentaras} value={irasid}/> */}
    </section>
  );
}
function Item(props) {
  console.log(props);
  return (
    <div>

      <div className="data"> {props.autorius}  {props.komentdata}</div>
      <div className="tekstas">{props.komentaras}</div>
      <div className="atsakymas">{props.atsakymas}</div>
      <AddatsakymForm atsakymas={props.atsakymas} value={props.id} />
      {/* <button  value={props.data.id} onClick={clicked}>Atsakyti</button> */}
    </div>
  );
}
function AddatsakymForm(props) {
  const initialFormState = { atsakymas: '' };
  const [komentaras, setKomentaras] = useState(initialFormState);

  const handleInputChange = event => {
    const { name, value } = event.target;

    setKomentaras({ ...komentaras, [name]: value })
  };

  const addAtsakymas = async event => {
    // console.log(komentaras);
    event.preventDefault();
    if (!komentaras.atsakymas) return;
    // console.log(komentaras);
    const response = await put(API_ENDPOINTS.uptadeKomentras + "/" + props.value, komentaras);

    // Atsakymas(response);
    setKomentaras(initialFormState)
  };

  return (
    <form onSubmit={addAtsakymas}>
      <div className="formlaukas">
        <label>Jūsų atsakymas </label>
        <input type="text" name="atsakymas" value={komentaras.atsakymas} onChange={handleInputChange} />
        <button className="formlaukas">Atsakyti</button>
      </div>

    </form>
  )
}
export default Komentarai;
