import React from 'react';
import './index.css';
import { API_ENDPOINTS } from "../utils/constants";
import { useEffect, useState } from "react";
import { API_DOMAIN } from "../utils/constants";

import Komentarai from '../Komentarai';


class Irasas extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            isupdated: false,
            irasas:{},
            loadingis:true
        }
    }
    componentDidMount() {
        const irasid = this.props.value;
        const url = API_ENDPOINTS.vienasirasas + "/" + irasid;  
        this.setState({ loadingis: true })
        fetch(`${API_DOMAIN}${url}`)
          .then(response => response.json())
          .then(data => {
            this.setState({
              irasid: data,
              loadingis: false
            })
          })
      }
    render() {
        // const irasid = this.props.value;
        // const url = API_ENDPOINTS.vienasirasas + "/" + irasid;
        // const [pavadinimas, setPavadinimas] = useState('pavadinibbbbbmas');
        // const [tekstas, setTekstas] = useState('tekstas');
        // const [irasodata, setIrasoata] = useState('2019-01-01');

            
        // useEffect(() => {
        //     const updateInfo = async () => {
        //         const data = await fetch(`${API_DOMAIN}${url}`)
        //             .then(result => result.json())

        //         setPavadinimas(data.pavadinimas);

        //         setTekstas(data.tekstas);
        //         setIrasoata(data.irasodata);
        //     };
        //     updateInfo();
        // }, []);
        // console.log();
        return (
            <div className="Irasas--visas">
                <h1 className="Irasas--Pavadinimas">{this.state.irasas.pavadinimas}</h1>
                <p className="Irasas--data">{this.state.irasas.irasodata}</p>
                <hr className="Irasas--separator" />
                <p className="Irasas--Tekstas">{this.state.irasas.tekstas}</p>
                <Komentarai value={this.state.irasas.irasid} />
            </div>
        )
    }
}
export default Irasas;
