import React, { useState } from 'react';
import './index.css';
import { useFetch } from "../utils/fetch-hook";
import { API_ENDPOINTS } from "../utils/constants";
import { API_DOMAIN } from "../utils/constants";
import { put } from "../utils/put";


class Komentarai extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      komentarailist: [],
      loadingis: false

    }
  }
  componentDidMount() {

    const url = API_ENDPOINTS.komentaras + "/" + this.props.value;
    this.setState({ loadingis: true })
    fetch(`${API_DOMAIN}${url}`)
    .then(response => response.json())
    .then(response => {
      this.setState({
        komentarailist: [],
        isloading: false
      })
    })

  }
  dochange(e) {
    // this.setState({
    //   komentarailist: Object.assign(
    //     {},
    //     this.state.irasas,
    //     { [e.target.name]: e.target.value }
    //   ),
    //   loadingis: {}
    // })
    console.log(this.state)
  }
  render() {
    const { komentarailist } = this.state;
    return (
    <section>
    <h4>Komentarai</h4>
    {this.state.isloading ? <div>Loading...</div> :
      komentarailist.map(data =>
        (
          <div>

          <div className="data"> {data.autorius}  {data.komentdata}</div>
          <div className="tekstas">{data.komentaras}</div>
          <div className="atsakymas">{data.atsakymas}</div>
          <div className="formlaukas">
            <label>Jūsų atsakymas </label>
            <input type="text" name="atsakymas" key={data.id} value={komentarailist.atsakymas} onChange={this.dochange} />
            <button className="formlaukas">Atsakyti</button>
          </div>
          {/* <button  value={props.data.id} onClick={clicked}>Atsakyti</button> */}
        </div>
        )
      )
    }
  </section>
    )
  }

}

export default Komentarai
