import React from 'react';
import './index.css';
import { API_ENDPOINTS } from "../utils/constants";
import { API_DOMAIN } from "../utils/constants";
import { async } from 'q';
import KomentaruAts from '../KomentaruAts';
import { put } from "../utils/put";
import { delrest } from "../utils/delete";

class IrasasEdit extends React.Component {
  constructor(props) {

    super(props)
    this.state = {
      irasas: {},
      loadingis: true
    }
    this.dochange = this.dochange.bind(this)
    this.updateIrasas = this.updateIrasas.bind(this)
  }


  componentDidMount() {
    const value = this.props.value;
    const url = API_ENDPOINTS.vienasirasas + "/" + value;
    this.setState({ loadingis: true })
    fetch(`${API_DOMAIN}${url}`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          irasas: data,
          loadingis: false
        })
      })
  }

  componentDidUpdate() {
    console.log("reiki kazkaip irasyti cia is didupdates")
  }
  dochange(event) {
    event.preventDefault();
    const { name, value } = event.target
    const field = "irasas." + name
    this.setState({ [field]: value })

  }
  updateIrasas() {
    console.log("reiki kazkaip irasyti")

  }
  render() {

    return (
      <div>
        {/* // <form onSubmit={this.updateIrasas}> */}
        <p>{this.state.irasas.irasodata}</p>
        <br />
        <div className="formlaukas">
          <label>Pavadinimas</label>
          <textarea type="text" cols="80" rows="3" name="pavadinimas" value={this.state.irasas["pavadinimas"]} onChange={this.dochange} />
        </div>
        <div className="formlaukas">
          <label>Tekstas</label>
          <textarea type="text" cols="80" rows="35" name="tekstas" value={this.state.irasas.tekstas} onChange={this.dochange} />
        </div>
        <button className="formlaukas" onClick={this.updateIrasas}>Įrašyti</button>

        {/* // </form> */}
      </div>
    )
  }
}

