import React, { useState } from 'react';
import './index.css';
import { useFetch } from "../utils/fetch-hook";
import { API_ENDPOINTS } from "../utils/constants";
import {API_DOMAIN} from "../utils/constants";
class Irasai extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      irasailist: [],
      isloading: true
    }
  }
  componentDidMount() {
    const url = API_ENDPOINTS.irasas
    this.setState({ lodingjoke: true })
    fetch(`${API_DOMAIN}${url}`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          irasailist: data.data,
          isloading: false
        })
      })
  }
  render() {
console.log(this.state.irasailist)
