import React, { Component, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import Irasai from './components/IrasaiList';
import Irasas from './components/Irasas';
import IrasasEdit from './components/IrasasEdit';
import IrasasAdd from './components/IrasasAdd';
import IrasasKitas from './components/IrasasEditKitas';

import { API_ENDPOINTS } from "./components/utils/constants";
import { API_DOMAIN } from "./components/utils/constants";

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      irasid: 0,
      loadingis: true
    }
    this.handler = this.handler.bind(this)
  }
  componentDidMount() {
    const url = API_ENDPOINTS.naujausias
    this.setState({ lodingjoke: true })
    fetch(`${API_DOMAIN}${url}`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          irasid: data.latestid,
          loadingis: false
        })
      })
  }
  render() {
    return (
      <div className="App" >
        <Header />
        <main>
          <div className="App--contenst row">
            <div className="App-content--left column1">

              <Irasai handler={this. handler}/>
            </div>
            <div className="App-content--right column2">

              {this.state.loadingis ? <h4>Loading... </h4> : <Irasas value={this.state.irasid} />}
            </div>
          </div>
        </main>
      </div>
    );
  }


  handler(idis) {
  console.log("as cia  "+idis)
    this.setState({ irasid: idis})
  console.log("dabar irasid "+this.state.irasid)
  };
}
export default App
