import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import Irasai from './components/IrasaiList';
import Irasas from './components/Irasas';
import IrasasEdit from './components/IrasasEdit';
import IrasasAdd from './components/IrasasAdd';
import IrasasKitas from './components/IrasasEditKitas';
import { useFetch } from "../utils/fetch-hook";
import { API_ENDPOINTS } from "../utils/constants";

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      irasid: 1
    }
  }
    render()
    {
      const  url = API_ENDPOINTS.naujausias
      useEffect(() => {
        const updateInfo = async () => {
          const data = await fetch(`${API_DOMAIN}${url}`)
            .then(result => result.json())
          this.setState({ irasid: data.latestid })
        }
        updateInfo();

      },
        []
      );
      return (
        <div className="App" >
          <Header />
          <main>
            <div className="App--contenst row">
              <div className="App-content--left column1">

                <Irasai />
              </div>
              <div className="App-content--right column2">

                <Irasas value={this.state.irasid} />
              </div>
            </div>
          </main>
        </div>
      );
    }
  }

  export default App
