import React, { useState } from 'react';
import './index.css';
import { useFetch } from "../utils/fetch-hook";
import { API_ENDPOINTS } from "../utils/constants";
import {API_DOMAIN} from "../utils/constants";
class Irasai extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      irasailist: [],
      isloading: true
    }
  }
  componentDidMount() {
    const url = API_ENDPOINTS.irasas
    this.setState({ lodingjoke: true })
    fetch(`${API_DOMAIN}${url}`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          irasailist: data,
          isloading: false
        })
      })
  }
  render() {

    console.log("from app")
    console.log(this.props)

    return (
      <section className="Irasas">
        <h3>Naujausi blogai</h3>
        {this.state.isloading ?
          <div>Loading...</div>
          :
          this.state.irasailist.map(data=>{
            return (
              <div>
                < h4> {data.pavadinimas}</h4>
                <div className="data"> {data.irasodata}</div>
                <div className="tekstas">
                  <p>{data.tekstas} </p>
                  <button className="formlaukas" onClick={() => this.props.handler(data.id)}>Daugiau</button>
                </div>
          
          
              </div>
            )
          })
        }

      </section>
    );
  }
}
function Item(props) {



  return (
    <div>
      < h4> {props.pavadinimas}</h4>
      <div className="data"> {props.irasodata}</div>
      <div className="tekstas">
        <p>{props.tekstas} </p>
        <button className="formlaukas" onClick={() => console.log(props)}>Daugiau</button>
      </div>


    </div>
  );
}
export default Irasai;
