import React, { useState } from 'react';
import './index.css';
import { useFetch } from "../utils/fetch-hook";
import { API_ENDPOINTS } from "../utils/constants";
import { API_DOMAIN } from "../utils/constants";
import { put } from "../utils/put";


class Komentarai extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      komentarailist: [],
      loadingis: false

    }
    this.dochange = this.dochange.bind(this)
  }
  componentDidMount(props) {

    const url = API_ENDPOINTS.komentaras + "/" + this.props.value;
    this.setState({ loadingis: true })
    fetch(`${API_DOMAIN}${url}`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          komentarailist:[],
          isloading: false
        })

      })
      .catch(e => {
        console.log(e);
        this.setState({ ...this.state, isFetching: false });
      });

  }
  componentDidUpdate(props) {

    const { refresh, value } = this.props;
    const url = API_ENDPOINTS.komentaras + "/" + value;
    if (props.refresh !== refresh) {
      this.setState({ loadingis: true })
      fetch(`${API_DOMAIN}${url}`)
        .then(response => response.json())
        .then(data => {
          this.setState({
            komentarailist: data,
            isloading: false
          })

        })
    }
  }


  dochange(id, e) {
  let eev=e.currentTarget
    console.log("id is  "+id)
   console.log(this.state.komentarailist[id-1])
   console.log(eev)
      this.setState(prevState => ({
        komentarailist: {
            ...prevState.komentarailist,
            [prevState.komentarailist[id-1].atsakymas]: e.target.value,
        },
        isloading: false
    }));
    console.log(this.state.komentarailist)
  }
  render() {
    const  komentarailistas  = this.state.komentarailistas;

    return (
      <section>
        <h4>Komentarai</h4>
        {this.state.isloading ? <div>Loading...</div> :
          komentarailistas.map(data =>
            (
              <div key={data.id}>

                <div className="data"> {data.autorius}  {data.komentdata} {data.id}</div>
                <div className="tekstas">{data.komentaras}</div>
                 <div className="formlaukas">
                  <label>JÅ«sÅ³ atsakymas </label>
                  <input type="text" name="atsakymas" key={data.id} value={data.atsakymas} onChange={(e) => this.dochange(data.id, e)} />
                  <button className="formlaukas">Atsakyti</button>
                </div>

              </div>
            )
          )
        }
      </section>
    )
  }

}

export default Komentarai
